int prod_signs(vector<int> arr) {
    // Step 1: Input Validation
    if (arr.empty()) {
        return -32768;
    }

    // Step 2: Sum of Magnitudes
    int sum_magnitudes = 0;
    for (int num : arr) {
        sum_magnitudes += abs(num);
    }

    // Step 3: Product of Signs
    int product_signs = 1;
    for (int num : arr) {
        if (num == 0) {
            product_signs = 0;
            break;
        } else if (num < 0) {
            product_signs *= -1;
        }
    }

    // Step 4: Combine Results
    return sum_magnitudes * product_signs;
}

