bool is_sorted(vector<int> lst) {
    bool is_ascending = true;
    unordered_map<int, int> count_map;

    // Check if the vector is sorted in ascending order
    for (int i = 0; i < lst.size() - 1; ++i) {
        if (lst[i] > lst[i + 1]) {
            is_ascending = false;
            break;
        }
    }

    // Count occurrences of each number
    for (int num : lst) {
        count_map[num]++;
        if (count_map[num] > 2) {
            return false;
        }
    }

    // Final check
    return is_ascending;
}

