int digits(int n) {
    int product = 1;
    bool hasOddDigit = false;

    while (n > 0) {
        int digit = n % 10; // Extract the last digit
        if (digit % 2 != 0) { // Check if the digit is odd
            product *= digit;
            hasOddDigit = true;
        }
        n /= 10; // Remove the last digit
    }

    if (hasOddDigit) {
        return product;
    } else {
        return 0;
    }
}


