vector<int> tri(int n) {
    // Initialize the vector with the base case
    vector<int> result(n + 1, 0);
    result[1] = 3;

    // Helper function to compute the Tribonacci value
    function<int(int)> computeTri = [&](int k) -> int {
        if (k == 1) return 3;
        if (k % 2 == 0) {
            result[k] = 1 + k / 2;
        } else {
            result[k] = computeTri(k - 1) + computeTri(k - 2) + computeTri(k + 1);
        }
        return result[k];
    };

    // Fill the vector
    for (int i = 2; i <= n; ++i) {
        computeTri(i);
    }

    // Return the result
    return result;
}

