        return -1;
    }

    // Initialize max_index
    int max_index = -1;

    // Iterate through the vector
    for (int i = 1; i < arr.size(); ++i) {
        // Condition check
        if (arr[i] < arr[i - 1]) {
            max_index = i;
        }
    }

    // Return the result
    return max_index;
}

