bool is_nested(string str) {
    stack<char> bracket_stack;
    bool nested_found = false;

    for (char ch : str) {
        if (ch == '[') {
            bracket_stack.push(ch);
        } else if (ch == ']') {
            if (!bracket_stack.empty()) {
                bracket_stack.pop();
                if (!bracket_stack.empty()) {
                    nested_found = true;
                }
            }
        }
    }

    return nested_found;
}


