vector<int> largest_smallest_integers(vector<int> lst) {
    int a = 0; // Default value for the largest negative integer
    int b = 0; // Default value for the smallest positive integer
    bool hasNegative = false;
    bool hasPositive = false;

    // Filter and process negative integers
    for (int num : lst) {
        if (num < 0) {
            if (!hasNegative || num > a) {
                a = num;
                hasNegative = true;
            }
        }
    }

    // Filter and process positive integers
    for (int num : lst) {
        if (num > 0) {
            if (!hasPositive || num < b) {
                b = num;
                hasPositive = true;
            }
        }
    }

    // Handle edge cases
    if (!hasNegative) {
        a = 0;
    }
    if (!hasPositive) {
        b = 0;
    }

    return {a, b};
}

