std::string getType(const boost::any& value) {
    if (value.type() == typeid(int)) return "int";
    else if (value.type() == typeid(float)) return "float";
    else if (value.type() == typeid(std::string)) return "string";
    else throw std::runtime_error("Unsupported type");
}

float stringToFloat(const std::string& str) {
    std::string cleanedStr = str;
    std::replace(cleanedStr.begin(), cleanedStr.end(), ',', '.');
    return std::stof(cleanedStr);
}

boost::any compare_one(boost::any a, boost::any b) {
    std::string typeA = getType(a);
    std::string typeB = getType(b);

    float numA, numB;

    if (typeA == "string") {
        numA = stringToFloat(boost::any_cast<std::string>(a));
    } else if (typeA == "int") {
        numA = static_cast<float>(boost::any_cast<int>(a));
    } else {
        numA = boost::any_cast<float>(a);
    }

    if (typeB == "string") {
        numB = stringToFloat(boost::any_cast<std::string>(b));
    } else if (typeB == "int") {
        numB = static_cast<float>(boost::any_cast<int>(b));
    } else {
        numB = boost::any_cast<float>(b);
    }

    if (numA == numB) {
        return "None";
    } else if (numA > numB) {
        return a;
    } else {
        return b;
    }
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (boost::any_cast<int>(compare_one(1, 2)) == 2);
    assert (boost::any_cast<double>(compare_one(1, 2.5))== 2.5);
    assert (boost::any_cast<int>(compare_one(2, 3)) == 3);
    assert (boost::any_cast<int>(compare_one(5, 6)) == 6);
    assert (boost::any_cast<string>(compare_one(1, string("2,3")))== "2,3");
    assert (boost::any_cast<string>(compare_one(string("5,1"), string("6"))) == "6");
    assert (boost::any_cast<string>(compare_one(string("1"), string("2"))) == "2");
    assert (boost::any_cast<string>(compare_one(string("1"), 1)) == "None");
}

