bool valid_date(string date) {
    // Step 1: Check if the date string is empty
    if (date.empty()) {
        return false;
    }

    // Step 2: Check the date string format
    regex date_format(R"(^(0[1-9]|1[0-2])-(0[1-9]|[12][0-9]|3[01])-\d{4}$)");
    if (!regex_match(date, date_format)) {
        return false;
    }

    // Step 3: Parse the date string
    int month, day, year;
    sscanf(date.c_str(), "%d-%d-%d", &month, &day, &year);

    // Step 4: Validate the month
    if (month < 1 || month > 12) {
        return false;
    }

    // Step 5: Validate the day based on the month
    switch (month) {
        case 1: case 3: case 5: case 7: case 8: case 10: case 12:
            if (day < 1 || day > 31) {
                return false;
            }
            break;
        case 4: case 6: case 9: case 11:
            if (day < 1 || day > 30) {
                return false;
            }
            break;
        case 2:
            if (day < 1 || day > 29) {
                return false;
            }
            break;
    }

    // Step 6: Return true if all checks pass
    return true;
}


