// Helper function to calculate factorial
long long factorial(int k) {
    if (k == 0 || k == 1) {
        return 1;
    }
    long long result = 1;
    for (int i = 2; i <= k; ++i) {
        result *= i;
    }
    return result;
}

// Main function to calculate Brazilian factorial
long long special_factorial(int n) {
    if (n <= 0) {
        throw std::invalid_argument("Input must be a positive integer.");
    }

    long long result = 1;
    for (int i = 1; i <= n; ++i) {
        result *= factorial(i);
    }
    return result;
}

