int sum_squares(vector<int> lst) {
    int sum = 0; // Initialize sum to 0

    // Iterate through the vector
    for (size_t i = 0; i < lst.size(); ++i) {
        int value = lst[i];

        // Check if the index is a multiple of 3
        if (i % 3 == 0) {
            value = value * value; // Square the value
        }
        // Check if the index is a multiple of 4 and not a multiple of 3
        else if (i % 4 == 0) {
            value = value * value * value; // Cube the value
        }

        // Add the transformed value to the sum
        sum += value;
    }

    // Return the final sum
    return sum;
}

