string file_name_check(string file_name) {
    // Step 1: Parse the file name
    size_t dot_pos = file_name.find('.');
    if (dot_pos == string::npos || file_name.find('.', dot_pos + 1) != string::npos) {
        return "No"; // More than one dot or no dot found
    }

    string name_part = file_name.substr(0, dot_pos);
    string extension_part = file_name.substr(dot_pos + 1);

    // Step 2: Validate the substring before the dot
    if (name_part.empty() || !isalpha(name_part[0])) {
        return "No"; // Empty or does not start with a letter
    }

    // Step 3: Validate the substring after the dot
    vector<string> valid_extensions = {"txt", "exe", "dll"};
    if (find(valid_extensions.begin(), valid_extensions.end(), extension_part) == valid_extensions.end()) {
        return "No"; // Invalid extension
    }

    // Step 4: Count the digits in the file name
    int digit_count = 0;
    for (char c : file_name) {
        if (isdigit(c)) {
            digit_count++;
        }
    }
    if (digit_count > 3) {
        return "No"; // More than three digits
    }

    // Step 5: Return the result
    return "Yes";
}


