// Function to check if a number is prime
bool is_prime(int n) {
    if (n <= 1) return false;
    if (n == 2) return true;
    if (n % 2 == 0) return false;
    for (int i = 3; i * i <= n; i += 2) {
        if (n % i == 0) return false;
    }
    return true;
}

// Function to filter words with prime lengths
std::string words_in_sentence(std::string sentence) {
    std::istringstream iss(sentence);
    std::vector<std::string> words;
    std::string word;

    // Split the sentence into words
    while (iss >> word) {
        words.push_back(word);
    }

    std::vector<std::string> prime_words;

    // Filter words with prime lengths
    for (const auto& w : words) {
        if (is_prime(w.length())) {
            prime_words.push_back(w);
        }
    }

    // Reconstruct the sentence
    std::string result;
    for (size_t i = 0; i < prime_words.size(); ++i) {
        result += prime_words[i];
        if (i < prime_words.size() - 1) {
            result += " ";
        }
    }

    return result;
}


