// Helper function to parse the fraction
void parseFraction(const string& fraction, int& numerator, int& denominator) {
    stringstream ss(fraction);
    string part;
    getline(ss, part, '/'); // Read numerator
    numerator = stoi(part);
    getline(ss, part); // Read denominator
    denominator = stoi(part);
}

// Main function to simplify the expression x * n
bool simplify(const string& x, const string& n) {
    int x_num, x_den, n_num, n_den;

    // Parse the fractions
    parseFraction(x, x_num, x_den);
    parseFraction(n, n_num, n_den);

    // Multiply the fractions
    int result_num = x_num * n_num;
    int result_den = x_den * n_den;

    // Check if the result is a whole number
    return (result_num % result_den == 0);
}

