using namespace std;

bool is_valid_move(int x, int y, int N) {
    return x >= 0 && x < N && y >= 0 && y < N;
}

void dfs(vector<vector<int>>& grid, int x, int y, int k, vector<int>& path, vector<int>& min_path) {
    if (k == 0) {
        if (min_path.empty() || path < min_path) {
            min_path = path;
        }
        return;
    }
    path.push_back(grid[x][y]);
    int dx[] = {0, 1, 0, -1};
    int dy[] = {1, 0, -1, 0};
    for (int i = 0; i < 4; ++i) {
        int nx = x + dx[i];
        int ny = y + dy[i];
        if (is_valid_move(nx, ny, grid.size())) {
            dfs(grid, nx, ny, k - 1, path, min_path);
        }
    }
    path.pop_back();
}

vector<int> minPath(vector<vector<int>> grid, int k) {
    int N = grid.size();
    vector<int> min_path;
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            vector<int> path;
            dfs(grid, i, j, k, path, min_path);
        }
    }
    return min_path;
}


