vector<string> vector_sort(vector<string> lst) {
    // Filter strings with even lengths
    vector<string> filtered_lst;
    for (const auto& str : lst) {
        if (str.length() % 2 == 0) {
            filtered_lst.push_back(str);
        }
    }

    // Sort the filtered strings
    sort(filtered_lst.begin(), filtered_lst.end(), [](const string& a, const string& b) {
        if (a.length() == b.length()) {
            return a < b; // Lexicographical order
        }
        return a.length() < b.length(); // Length order
    });

    // Return the sorted vector
    return filtered_lst;
}

