// Function to generate the vector a
std::vector<int> generate_vector(int n) {
    std::vector<int> a(n);
    for (int i = 1; i <= n; ++i) {
        a[i-1] = i * i - i + 1;
    }
    return a;
}

// Function to count valid triples
int count_valid_triples(const std::vector<int>& a) {
    int count = 0;
    int n = a.size();
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            for (int k = j + 1; k < n; ++k) {
                if ((a[i] + a[j] + a[k]) % 3 == 0) {
                    ++count;
                }
            }
        }
    }
    return count;
}

// Main function to get the number of valid triples
int get_max_triples(int n) {
    std::vector<int> a = generate_vector(n);
    return count_valid_triples(a);
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (get_max_triples(5) == 1);
    assert (get_max_triples(6) == 4);
    assert (get_max_triples(10) == 36);
    assert (get_max_triples(100) == 53361);
}

