const vector<string> planets = {"Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"};

bool isValidPlanet(const string& planet) {
    return find(planets.begin(), planets.end(), planet) != planets.end();
}

vector<string> bf(string planet1, string planet2) {
    if (!isValidPlanet(planet1) || !isValidPlanet(planet2)) {
        return {};
    }

    int pos1 = find(planets.begin(), planets.end(), planet1) - planets.begin();
    int pos2 = find(planets.begin(), planets.end(), planet2) - planets.begin();

    if (pos1 > pos2) {
        swap(pos1, pos2);
    }

    vector<string> result;
    for (int i = pos1 + 1; i < pos2; ++i) {
        result.push_back(planets[i]);
    }

    return result;
}


