// Helper function to calculate the sum of digits
int sum_of_digits(int num) {
    int sum = 0;
    bool is_negative = num < 0;
    while (num != 0) {
        int digit = num % 10;
        if (is_negative && num < 0) {
            digit *= -1; // Consider the first digit as negative
            is_negative = false;
        }
        sum += digit;
        num /= 10;
    }
    return sum;
}

// Struct for pairing integers with their indices
struct IndexedInt {
    int value;
    int index;
};

// Custom comparator
bool custom_comparator(const IndexedInt& a, const IndexedInt& b) {
    int sum_a = sum_of_digits(a.value);
    int sum_b = sum_of_digits(b.value);
    if (sum_a == sum_b) {
        return a.index < b.index;
    }
    return sum_a < sum_b;
}

// Main function to sort the vector
std::vector<int> order_by_points(std::vector<int> nums) {
    // Transform the input vector into a vector of IndexedInt
    std::vector<IndexedInt> indexed_nums;
    for (int i = 0; i < nums.size(); ++i) {
        indexed_nums.push_back({nums[i], i});
    }

    // Sort the transformed vector using the custom comparator
    std::sort(indexed_nums.begin(), indexed_nums.end(), custom_comparator);

    // Extract the sorted integers
    std::vector<int> sorted_nums;
    for (const auto& indexed_num : indexed_nums) {
        sorted_nums.push_back(indexed_num.value);
    }

    return sorted_nums;
}

