// Helper function to generate all rotations of a string
vector<string> generate_rotations(const string& b) {
    vector<string> rotations;
    int n = b.length();
    for (int i = 0; i < n; ++i) {
        string rotated = b.substr(i) + b.substr(0, i);
        rotations.push_back(rotated);
    }
    return rotations;
}

// Helper function to check if a string is a substring of another string
bool is_substring(const string& a, const string& b) {
    return a.find(b) != string::npos;
}

// Main function to check if any rotation of b is a substring of a
bool cycpattern_check(const string& a, const string& b) {
    vector<string> rotations = generate_rotations(b);
    for (const string& rotation : rotations) {
        if (is_substring(a, rotation)) {
            return true;
        }
    }
    return false;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert  (cycpattern_check("xyzw","xyw") == false );
    assert  (cycpattern_check("yello","ell") == true );
    assert  (cycpattern_check("whattup","ptut") == false );
    assert  (cycpattern_check("efef","fee") == true );
    assert  (cycpattern_check("abab","aabb") == false );
    assert  (cycpattern_check("winemtt","tinem") == true );
}

