long long double_the_difference(vector<float> lst) {
    long long sum_of_squares = 0;

    // Step 1: Input Validation
    if (lst.empty()) {
        return 0;
    }

    // Step 2: Filtering and Processing
    for (float num : lst) {
        // Check if the number is an integer
        if (num > 0 && floor(num) == num) {
            int int_num = static_cast<int>(num);
            // Check if the number is odd
            if (int_num % 2 != 0) {
                // Compute the square and add to the sum
                sum_of_squares += int_num * int_num;
            }
        }
    }

    // Step 3: Return the Result
    return sum_of_squares;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (double_the_difference({}) == 0);
    assert (double_the_difference({5, 4}) == 25);
    assert (double_the_difference({0.1, 0.2, 0.3}) == 0 );
    assert (double_the_difference({-10, -20, -30}) == 0 );
    assert (double_the_difference({-1, -2, 8}) == 0);
    assert (double_the_difference({0.2, 3, 5}) == 34);


    long long odd_sum=0;
    vector<float> lst={};

    for (int i=-99;i<100;i+=2)
    {
        lst.push_back(i+0.0);
        if (i>0 and i%2==1) odd_sum+=i*i;
    }

    assert (double_the_difference(lst) == odd_sum );
}

