using namespace std;

vector<int> eat(int number, int need, int remaining) {
    // Input validation
    if (number < 0 || number > 1000 || need < 0 || need > 1000 || remaining < 0 || remaining > 1000) {
        throw invalid_argument("Input values must be between 0 and 1000.");
    }

    // Calculate total carrots eaten
    int total_eaten = number + min(need, remaining);

    // Calculate remaining carrots
    int remaining_after_meals = max(0, remaining - need);

    // Return the result
    return {total_eaten, remaining_after_meals};
}


