using namespace std;

bool right_angle_triangle(float a, float b, float c) {
    // Step 1: Input Validation
    if (a <= 0 || b <= 0 || c <= 0) {
        return false; // Sides must be positive
    }
    if (a + b <= c || a + c <= b || b + c <= a) {
        return false; // Triangle inequality theorem
    }

    // Step 2: Identify the Hypotenuse
    float hypotenuse, side1, side2;
    if (a >= b && a >= c) {
        hypotenuse = a;
        side1 = b;
        side2 = c;
    } else if (b >= a && b >= c) {
        hypotenuse = b;
        side1 = a;
        side2 = c;
    } else {
        hypotenuse = c;
        side1 = a;
        side2 = b;
    }

    // Step 3: Apply the Pythagorean Theorem
    float hypotenuse_squared = pow(hypotenuse, 2);
    float sum_of_squares = pow(side1, 2) + pow(side2, 2);

    // Step 4: Return the Result
    return hypotenuse_squared == sum_of_squares;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (right_angle_triangle(3, 4, 5) == true);
    assert (right_angle_triangle(1, 2, 3) == false);
    assert (right_angle_triangle(10, 6, 8) == true);
    assert (right_angle_triangle(2, 2, 2) == false);
    assert (right_angle_triangle(7, 24, 25) == true);
    assert (right_angle_triangle(10, 5, 7) == false);
    assert (right_angle_triangle(5, 12, 13) == true);
    assert (right_angle_triangle(15, 8, 17) == true);
    assert (right_angle_triangle(48, 55, 73) == true);
    assert (right_angle_triangle(1, 1, 1) == false);
    assert (right_angle_triangle(2, 2, 10) == false);
}

