using namespace std;

string solve(string s) {
    bool containsLetter = false;
    string result = "";

    // Iterate through the string
    for (char c : s) {
        if (isalpha(c)) {  // Check if the character is a letter
            containsLetter = true;
            if (islower(c)) {
                result += toupper(c);  // Convert lowercase to uppercase
            } else {
                result += tolower(c);  // Convert uppercase to lowercase
            }
        } else {
            result += c;  // Keep non-letter characters as is
        }
    }

    // If no letters are found, reverse the string
    if (!containsLetter) {
        reverse(result.begin(), result.end());
    }

    return result;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (solve("AsDf") == "aSdF");
    assert (solve("1234") == "4321");
    assert (solve("ab") == "AB");
    assert (solve("#a@C") == "#A@c");
    assert (solve("#AsdfW^45") == "#aSDFw^45");
    assert (solve("#6@2") == "2@6#");
    assert (solve("#$a^D") == "#$A^d");
    assert (solve("#ccc") == "#CCC");
}

