using namespace std;

// Helper function to count unique characters in a string
int count_unique_chars(const string& str) {
    set<char> unique_chars(str.begin(), str.end());
    return unique_chars.size();
}

// Main function to find the word with the maximum number of unique characters
string find_max(vector<string> words) {
    int max_unique = 0;
    vector<string> candidates;

    // Iterate through the vector of strings
    for (const auto& word : words) {
        int unique_count = count_unique_chars(word);
        if (unique_count > max_unique) {
            max_unique = unique_count;
            candidates.clear();
            candidates.push_back(word);
        } else if (unique_count == max_unique) {
            candidates.push_back(word);
        }
    }

    // Sort the candidates lexicographically
    sort(candidates.begin(), candidates.end());

    // Return the first string in the sorted list
    return candidates.front();
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert ((find_max({"name", "of", "string"}) == "string"));
    assert ((find_max({"name", "enam", "game"}) == "enam"));
    assert ((find_max({"aaaaaaa", "bb", "cc"}) == "aaaaaaa"));
    assert ((find_max({"abc", "cba"}) == "abc"));
    assert ((find_max({"play", "this", "game", "of","footbott"}) == "footbott"));
    assert ((find_max({"we", "are", "gonna", "rock"}) == "gonna"));
    assert ((find_max({"we", "are", "a", "mad", "nation"}) == "nation"));
    assert ((find_max({"this", "is", "a", "prrk"}) == "this"));
    assert ((find_max({"b"}) == "b"));
    assert ((find_max({"play", "play", "play"}) == "play"));
}

