#include <string>
#include <stack>
#include <sstream>
#include <cmath>
using namespace std;

bool validate_input(const vector<string>& operators, const vector<int>& operands) {
    if (operators.size() != operands.size() - 1) return false;
    for (const auto& op : operators) {
        if (op != "+" && op != "-" && op != "*" && op != "//" && op != "**") return false;
    }
    for (const auto& num : operands) {
        if (num < 0) return false;
    }
    return true;
}

string construct_expression(const vector<string>& operators, const vector<int>& operands) {
    string expression;
    for (size_t i = 0; i < operands.size(); ++i) {
        expression += to_string(operands[i]);
        if (i < operators.size()) {
            expression += " " + operators[i] + " ";
        }
    }
    return expression;
}

int precedence(const string& op) {
    if (op == "+" || op == "-") return 1;
    if (op == "*" || op == "//") return 2;
    if (op == "**") return 3;
    return 0;
}

int apply_op(const string& op, int a, int b) {
    if (op == "+") return a + b;
    if (op == "-") return a - b;
    if (op == "*") return a * b;
    if (op == "//") return a / b;
    if (op == "**") return pow(a, b);
    return 0;
}

int evaluate_expression(const string& expression) {
    stack<int> values;
    stack<string> ops;

    istringstream iss(expression);
    string token;
    while (iss >> token) {
        if (isdigit(token[0]) || (token.size() > 1 && isdigit(token[1]))) {
            values.push(stoi(token));
        } else {
            while (!ops.empty() && precedence(ops.top()) >= precedence(token)) {
                int val2 = values.top(); values.pop();
                int val1 = values.top(); values.pop();
                values.push(apply_op(ops.top(), val1, val2));
                ops.pop();
            }
            ops.push(token);
        }
    }

    while (!ops.empty()) {
        int val2 = values.top(); values.pop();
        int val1 = values.top(); values.pop();
        values.push(apply_op(ops.top(), val1, val2));
        ops.pop();
    }

    return values.top();
}

int do_algebra(vector<string> operators, vector<int> operands) {
    if (!validate_input(operators, operands)) {
        throw invalid_argument("Invalid input");
    }

    string expression = construct_expression(operators, operands);
    return evaluate_expression(expression);
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (do_algebra({"**", "*", "+"}, {2, 3, 4, 5}) == 37);
    assert (do_algebra({"+", "*", "-"}, {2, 3, 4, 5}) == 9);
    assert (do_algebra({"//", "*"}, {7, 3, 4}) == 8);
}

