using namespace std;

vector<int> generate_integers(int a, int b) {
    if (a < 0 || b < 0) {
        throw invalid_argument("Both a and b must be positive integers.");
    }
    if (a > b) {
        swap(a, b);
    }

    vector<int> even_digits = {0, 2, 4, 6, 8};
    vector<int> result;

    for (int digit : even_digits) {
        if (digit >= a && digit <= b) {
            result.push_back(digit);
        }
    }

    return result;
}


