using namespace std;

optional<string> longest(const vector<string>& strings) {
    if (strings.empty()) {
        return nullopt; // Return None if the vector is empty
    }

    const string* longest_str = &strings[0]; // Initialize with the first string

    for (const auto& str : strings) {
        if (str.length() > longest_str->length()) {
            longest_str = &str; // Update the longest string
        }
    }

    return *longest_str; // Return the longest string
}

// Example usage

