using namespace std;

vector<int> parse_nested_parens(string paren_string) {
    vector<int> result;
    stringstream ss(paren_string);
    string group;

    while (getline(ss, group, ' ')) {
        int max_depth = 0;
        int current_depth = 0;

        for (char c : group) {
            if (c == '(') {
                current_depth++;
                max_depth = max(max_depth, current_depth);
            } else if (c == ')') {
                current_depth--;
            }
        }

        result.push_back(max_depth);
    }

    return result;
}


