bool below_zero(const vector<int>& operations) {
    int balance = 0; // Start with zero balance

    for (int op : operations) {
        balance += op; // Update the balance with each operation
        if (balance < 0) {
            return true; // Return true if balance falls below zero
        }
    }

    return false; // Return false if balance never falls below zero
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (below_zero({}) == false);
    assert (below_zero({1, 2, -3, 1, 2, -3}) == false);
    assert (below_zero({1, 2, -4, 5, 6}) == true);
    assert (below_zero({1, -1, 2, -2, 5, -5, 4, -4}) == false);
    assert (below_zero({1, -1, 2, -2, 5, -5, 4, -5}) == true);
    assert (below_zero({1, -2, 2, -2, 5, -5, 4, -4}) == true);
}

