vector<int> rolling_max(vector<int> numbers) {
    vector<int> result;
    if (numbers.empty()) return result; // Handle empty input

    int current_max = numbers[0];
    for (int num : numbers) {
        if (num > current_max) {
            current_max = num;
        }
        result.push_back(current_max);
    }
    return result;
}

// Function to test the rolling_max function
void test_rolling_max() {
    vector<int> input = {1, 2, 3, 2, 3, 4, 2};
    vector<int> expected_output = {1, 2, 3, 3, 3, 4, 4};
    vector<int> output = rolling_max(input);

    if (output == expected_output) {
        cout << "Test passed!" << endl;
    } else {
        cout << "Test failed!" << endl;
    }
}


