using namespace std;

string string_xor(string a, string b) {
    string result = "";
    // Ensure both strings are of the same length
    if (a.length() != b.length()) {
        return "Error: Strings must be of the same length";
    }

    // Perform XOR operation on each pair of characters
    for (size_t i = 0; i < a.length(); ++i) {
        // Convert characters to integers (0 or 1)
        int bitA = a[i] - '0';
        int bitB = b[i] - '0';

        // Compute XOR and convert back to character
        char xorResult = (bitA ^ bitB) + '0';

        // Append the result to the result string
        result += xorResult;
    }

    return result;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (string_xor("111000", "101010") == "010010");
     assert (string_xor("1", "1") == "0");
    assert (string_xor("0101", "0000") == "0101");

}

