bool is_palindrome(const string& str) {
    // Test if given string is a palindrome
    string s(str.rbegin(), str.rend());
    return s == str;
}

string make_palindrome(const string& str) {
    int n = str.length();
    for (int i = 0; i < n; ++i) {
        // Check if the suffix starting from i is a palindrome
        if (is_palindrome(str.substr(i))) {
            // If it is, then the non-palindromic prefix is the substring from 0 to i-1
            string prefix = str.substr(0, i);
            // Reverse the prefix and append it to the original string
            string reversed_prefix = string(prefix.rbegin(), prefix.rend());
            return str + reversed_prefix;
        }
    }
    // If no palindromic suffix is found (which won't happen because an empty string is a palindrome)
    return str;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (make_palindrome("") == "");
    assert (make_palindrome("x") == "x");
     assert (make_palindrome("xyz") == "xyzyx");
     assert (make_palindrome("xyx") == "xyx") ;
     assert (make_palindrome("jerry") == "jerryrrej");
}

