int count_distinct_characters(const string& str) {
    // Convert the string to lowercase
    string lower_str = str;
    transform(lower_str.begin(), lower_str.end(), lower_str.begin(), ::tolower);

    // Use a set to store unique characters
    set<char> unique_chars(lower_str.begin(), lower_str.end());

    // The size of the set is the number of distinct characters
    return unique_chars.size();
}

// Function to test the solution
void test() {
    cout << count_distinct_characters("xyzXYZ") << endl; // Output: 3
    cout << count_distinct_characters("Jerry") << endl;  // Output: 4
}


