vector<int> parse_music(string music_string) {
    vector<int> beats;
    int i = 0;
    while (i < music_string.length()) {
        if (music_string[i] == 'o') {
            if (i + 1 < music_string.length() && music_string[i + 1] == '|') {
                // Half note
                beats.push_back(2);
                i += 2; // Skip "o|"
            } else {
                // Whole note
                beats.push_back(4);
                i += 1; // Skip "o"
            }
        } else if (music_string[i] == '.') {
            if (i + 1 < music_string.length() && music_string[i + 1] == '|') {
                // Quarter note
                beats.push_back(1);
                i += 2; // Skip ".|"
            } else {
                // Invalid note
                break;
            }
        } else {
            // Skip spaces and other characters
            i += 1;
        }
    }
    return beats;
}

