int how_many_times(const string& str, const string& substring) {
    if (substring.empty()) return 0; // If the substring is empty, return 0

    int count = 0;
    size_t pos = 0;

    while ((pos = str.find(substring, pos)) != string::npos) {
        count++;
        pos++; // Move to the next character to allow for overlapping matches
    }

    return count;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (how_many_times("", "x") == 0);
    assert (how_many_times("xyxyxyx", "x") == 4);
     assert (how_many_times("cacacacac", "cac") == 4);
    assert (how_many_times("john doe", "john") == 1);
}

