vector<float> find_closest_elements(vector<float> numbers) {
    // Ensure the vector has at least two elements
    if (numbers.size() < 2) {
        throw invalid_argument("Vector must contain at least two elements");
    }

    // Sort the vector
    sort(numbers.begin(), numbers.end());

    // Initialize variables to store the closest pair and the minimum difference
    float min_diff = FLT_MAX; // Use FLT_MAX from <cfloat> for maximum floating-point value
    vector<float> closest_pair(2);

    // Iterate through the sorted vector to find the closest pair
    for (size_t i = 0; i < numbers.size() - 1; ++i) {
        float diff = fabs(numbers[i + 1] - numbers[i]);
        if (diff < min_diff) {
            min_diff = diff;
            closest_pair[0] = numbers[i];
            closest_pair[1] = numbers[i + 1];
        }
    }

    return closest_pair;
}

