using namespace std;

// Function to sort the numbers represented as words
string sort_numbers(string numbers) {
    // Mapping of number words to their numeric values
    map<string, int> number_map = {
        {"zero", 0}, {"one", 1}, {"two", 2}, {"three", 3},
        {"four", 4}, {"five", 5}, {"six", 6}, {"seven", 7},
        {"eight", 8}, {"nine", 9}
    };

    // Vector to store the words
    vector<string> words;
    stringstream ss(numbers);
    string word;

    // Split the input string into words
    while (ss >> word) {
        words.push_back(word);
    }

    // Sort the words based on their numeric values
    sort(words.begin(), words.end(), [&](const string &a, const string &b) {
        return number_map[a] < number_map[b];
    });

    // Join the sorted words back into a single string
    string result;
    for (size_t i = 0; i < words.size(); ++i) {
        if (i > 0) {
            result += " ";
        }
        result += words[i];
    }

    return result;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (sort_numbers("") == "");
    assert (sort_numbers("three") == "three");
    assert (sort_numbers("three five nine") == "three five nine");
     assert (sort_numbers("five zero four seven nine eight") == "zero four five seven eight nine");
      assert (sort_numbers("six five four three two one zero") == "zero one two three four five six");
}

