vector<int> remove_duplicates(vector<int> numbers) {
    // Step 1: Count the occurrences of each number
    unordered_map<int, int> count;
    for (int num : numbers) {
        count[num]++;
    }

    // Step 2: Create a new vector to store the result
    vector<int> result;
    for (int num : numbers) {
        // Only add numbers that appear exactly once
        if (count[num] == 1) {
            result.push_back(num);
        }
    }

    return result;
}

// Function to test the remove_duplicates function
void test_remove_duplicates() {
    vector<int> input = {1, 2, 3, 2, 4};
    vector<int> expected_output = {1, 3, 4};
    vector<int> output = remove_duplicates(input);

    if (output == expected_output) {
        cout << "Test passed." << endl;
    } else {
        cout << "Test failed." << endl;
    }
}


