using namespace std;

double poly(const vector<double>& xs, double x) {
    double sum = 0;
    for (size_t i = 0; i < xs.size(); ++i) {
        sum += xs[i] * pow(x, i);
    }
    return sum;
}

double poly_derivative(const vector<double>& xs, double x) {
    double sum = 0;
    for (size_t i = 1; i < xs.size(); ++i) {
        sum += i * xs[i] * pow(x, i - 1);
    }
    return sum;
}

double find_zero(const vector<double>& xs) {
    // Initial guess
    double x = 0.0;
    double epsilon = 1e-6; // Tolerance for convergence
    double max_iterations = 1000; // Maximum number of iterations

    for (int i = 0; i < max_iterations; ++i) {
        double f_x = poly(xs, x);
        double f_prime_x = poly_derivative(xs, x);

        if (fabs(f_prime_x) < epsilon) {
            // Derivative is too small, avoid division by zero
            break;
        }

        double next_x = x - f_x / f_prime_x;

        if (fabs(next_x - x) < epsilon) {
            // Convergence criterion met
            return next_x;
        }

        x = next_x;
    }

    // If no convergence, return the last value of x
    return x;
}


#undef NDEBUG
#include<assert.h>
int main(){

    double solution;
    int ncoeff;
    for (int i=0;i<100;i++)
    {
        ncoeff = 2 * (1+rand()%4);
        vector<double> coeffs = {};
        for (int j=0;j<ncoeff;j++)
        {
            double coeff = -10+rand()%21;
            if (coeff == 0) coeff = 1;
            coeffs.push_back(coeff);
        }

        solution = find_zero(coeffs);
        assert (abs(poly(coeffs, solution))< 1e-3);
    }
}

