using namespace std;

bool triples_sum_to_zero(vector<int> l) {
    // If the vector has less than 3 elements, it's impossible to have three elements sum to zero
    if (l.size() < 3) {
        return false;
    }

    // Sort the vector to use the two-pointer technique
    sort(l.begin(), l.end());

    // Iterate through the vector, using each element as a potential first element of the triplet
    for (int i = 0; i < l.size() - 2; ++i) {
        // Skip duplicate elements to avoid checking the same triplet multiple times
        if (i > 0 && l[i] == l[i - 1]) {
            continue;
        }

        int left = i + 1;
        int right = l.size() - 1;

        // Use two pointers to find the other two elements
        while (left < right) {
            int sum = l[i] + l[left] + l[right];

            if (sum == 0) {
                return true; // Found a triplet that sums to zero
            } else if (sum < 0) {
                ++left; // Move the left pointer to the right to increase the sum
            } else {
                --right; // Move the right pointer to the left to decrease the sum
            }

            // Skip duplicate elements to avoid checking the same triplet multiple times
            while (left < right && l[left] == l[left - 1]) {
                ++left;
            }
            while (left < right && l[right] == l[right + 1]) {
                --right;
            }
        }
    }

    return false; // No triplet found that sums to zero
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (triples_sum_to_zero({1, 3, 5, 0}) == false);
    assert (triples_sum_to_zero({1, 3, 5, -1}) == false);
    assert (triples_sum_to_zero({1, 3, -2, 1}) == true);
    assert (triples_sum_to_zero({1, 2, 3, 7}) == false);
    assert (triples_sum_to_zero({1, 2, 5, 7}) == false);
    assert (triples_sum_to_zero({2, 4, -5, 3, 9, 7}) == true);
    assert (triples_sum_to_zero({1}) == false);
    assert (triples_sum_to_zero({1, 3, 5, -100}) == false);
    assert (triples_sum_to_zero({100, 3, 5, -100}) == false);
}

