// Function to check if a number is prime
bool is_prime(int num) {
    if (num <= 1) return false;
    if (num == 2) return true;
    if (num % 2 == 0) return false;
    for (int i = 3; i <= sqrt(num); i += 2) {
        if (num % i == 0) return false;
    }
    return true;
}

// Function to return the n-th prime Fibonacci number
int prime_fib(int n) {
    int count = 0; // To keep track of the number of prime Fibonacci numbers found
    int a = 0, b = 1; // First two Fibonacci numbers

    while (true) {
        int fib = a + b; // Next Fibonacci number
        a = b;
        b = fib;

        if (is_prime(fib)) {
            count++;
            if (count == n) {
                return fib;
            }
        }
    }
}


