using namespace std;

float median(vector<float> l) {
    // Step 1: Sort the vector
    sort(l.begin(), l.end());

    int n = l.size();

    // Step 2: Check if the number of elements is odd or even
    if (n % 2 == 0) {
        // Step 4: If even, return the average of the two middle elements
        return (l[n / 2 - 1] + l[n / 2]) / 2.0;
    } else {
        // Step 3: If odd, return the middle element
        return l[n / 2];
    }
}


