int modp(int n, int p) {
    int result = 1;
    int base = 2;

    while (n > 0) {
        if (n % 2 == 1) {  // If n is odd, multiply the result by the current base
            result = (result * base) % p;
        }
        base = (base * base) % p;  // Square the base
        n = n / 2;  // Divide n by 2
    }

    return result;
}


