using namespace std;

bool monotonic(vector<float> l) {
    // If the vector is empty or has only one element, it is trivially monotonic
    if (l.size() <= 1) {
        return true;
    }

    // Determine the initial trend
    bool increasing = l[1] >= l[0];
    bool decreasing = l[1] <= l[0];

    // If the first two elements are equal, we need to check further
    if (increasing && decreasing) {
        for (size_t i = 2; i < l.size(); ++i) {
            if (l[i] > l[i - 1]) {
                increasing = true;
                decreasing = false;
                break;
            } else if (l[i] < l[i - 1]) {
                increasing = false;
                decreasing = true;
                break;
            }
        }
    }

    // Check the rest of the vector based on the determined trend
    for (size_t i = 2; i < l.size(); ++i) {
        if (increasing && l[i] < l[i - 1]) {
            return false;
        }
        if (decreasing && l[i] > l[i - 1]) {
            return false;
        }
    }

    return true;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (monotonic({1, 2, 4, 10}) == true);
    assert (monotonic({1, 2, 4, 20}) == true);
    assert (monotonic({1, 20, 4, 10}) == false);
    assert (monotonic({4, 1, 0, -10}) == true);
    assert (monotonic({4, 1, 1, 0}) == true);
    assert (monotonic({1, 2, 3, 2, 5, 60}) == false);
    assert (monotonic({1, 2, 3, 4, 5, 60}) == true);
    assert (monotonic({9, 9, 9, 9}) == true);
}

