vector<int> common(vector<int> l1, vector<int> l2) {
    // Step 1: Sort both vectors
    sort(l1.begin(), l1.end());
    sort(l2.begin(), l2.end());

    vector<int> result;
    int i = 0, j = 0;

    // Step 2: Use two pointers to find common elements
    while (i < l1.size() && j < l2.size()) {
        if (l1[i] == l2[j]) {
            // Step 3: Add to result if it's not already in the result
            if (result.empty() || result.back() != l1[i]) {
                result.push_back(l1[i]);
            }
            i++;
            j++;
        } else if (l1[i] < l2[j]) {
            i++;
        } else {
            j++;
        }
    }

    return result;
}

// Function to test the common function
