int fibfib(int n) {
    if (n == 0 || n == 1) return 0;
    if (n == 2) return 1;

    // Initialize a vector to store the FibFib values up to n
    std::vector<int> dp(n + 1, 0);
    dp[0] = 0;
    dp[1] = 0;
    dp[2] = 1;

    // Compute the FibFib values iteratively
    for (int i = 3; i <= n; ++i) {
        dp[i] = dp[i - 1] + dp[i - 2] + dp[i - 3];
    }

    return dp[n];
}


