using namespace std;

int fruit_distribution(string s, int n) {
    // Create a stringstream from the input string
    stringstream ss(s);
    string token;
    int apples = 0, oranges = 0;

    // Parse the string to extract the number of apples and oranges
    while (ss >> token) {
        if (token == "apples" && !ss.eof()) {
            ss >> token; // Read the number before "apples"
            apples = stoi(token);
        } else if (token == "oranges" && !ss.eof()) {
            ss >> token; // Read the number before "oranges"
            oranges = stoi(token);
        }
    }

    // Calculate the number of mangoes
    int mangoes = n - apples - oranges;

    return mangoes;
}

// Function to test the solution
void test_fruit_distribution() {
    cout << fruit_distribution("5 apples and 6 oranges", 19) << endl; // Expected output: 8
    cout << fruit_distribution("0 apples and 1 oranges", 3) << endl;  // Expected output: 2
    cout << fruit_distribution("2 apples and 3 oranges", 100) << endl; // Expected output: 95
    cout << fruit_distribution("100 apples and 1 oranges", 120) << endl; // Expected output: 19
}


