    while (!lst.empty()) {
        // Find the minimum element
        auto min_it = min_element(lst.begin(), lst.end());
        result.push_back(*min_it);
        lst.erase(min_it);

        if (!lst.empty()) {
            // Find the maximum element
            auto max_it = max_element(lst.begin(), lst.end());
            result.push_back(*max_it);
            lst.erase(max_it);
        }
    }
    return result;
}

