bool is_palindromic(const vector<int>& q) {
    int n = q.size();
    for (int i = 0; i < n / 2; ++i) {
        if (q[i] != q[n - 1 - i]) {
            return false;
        }
    }
    return true;
}

bool will_it_fly(vector<int> q, int w) {
    // Check if the vector is palindromic
    if (!is_palindromic(q)) {
        return false;
    }

    // Calculate the sum of the elements
    int sum = 0;
    for (int num : q) {
        sum += num;
    }

    // Check if the sum is within the weight limit
    return sum <= w;
}

